    filter: "Datadog Read Only Role",
});
// Create a new AuthN mapping
const devRoRoleMapping = new datadog.AuthnMapping("devRoRoleMapping", {
    key: "Member-of",
    value: "Development",
    role: roRole.then(roRole => roRole.id),
});
