// Metric-Based SLO
// Create a new Datadog service level objective
const foo = new datadog.ServiceLevelObjective("foo", {
    name: "Example Metric SLO",
    type: "metric",
    description: "My custom metric SLO",
    query: {
        numerator: "sum:my.custom.count.metric{type:good_events}.as_count()",
        denominator: "sum:my.custom.count.metric{*}.as_count()",
    },
    thresholds: [
        {
            timeframe: "7d",
            target: 99.9,
            warning: 99.99,
        },
        {
            timeframe: "30d",
            target: 99.9,
            warning: 99.99,
        },
    ],
    timeframe: "30d",
    targetThreshold: 99.9,
    warningThreshold: 99.99,
    tags: [
        "foo:bar",
        "baz",
    ],
});
// Monitor-Based SLO
// Create a new Datadog service level objective
const bar = new datadog.ServiceLevelObjective("bar", {
    name: "Example Monitor SLO",
    type: "monitor",
    description: "My custom monitor SLO",
    monitorIds: [
        1,
        2,
        3,
    ],
    thresholds: [
        {
            timeframe: "7d",
            target: 99.9,
            warning: 99.99,
        },
        {
            timeframe: "30d",
            target: 99.9,
            warning: 99.99,
        },
    ],
    timeframe: "30d",
    targetThreshold: 99.9,
    warningThreshold: 99.99,
    tags: [
        "foo:bar",
        "baz",
    ],
});
const timeSliceSlo = new datadog.ServiceLevelObjective("time_slice_slo", {
    name: "Example Time Slice SLO",
    type: "time_slice",
    description: "My custom time slice SLO",
    sliSpecification: {
        timeSlice: {
            query: {
                formula: {
                    formulaExpression: "query1",
                },
                queries: [{
                    metricQuery: {
                        name: "query1",
                        query: "avg:my.custom.count.metric{*}.as_count()",
                    },
                }],
            },
            comparator: ">",
            threshold: 0.9,
        },
    },
    thresholds: [{
        timeframe: "7d",
        target: 99.9,
        warning: 99.99,
    }],
    timeframe: "7d",
    targetThreshold: 99.9,
    warningThreshold: 99.99,
    tags: [
        "service:myservice",
        "team:myteam",
    ],
});
