    description: "My custom metric SLO",
    name: "Example Metric SLO",
    query: {
        denominator: "sum:my.custom.count.metric{*}.as_count()",
        numerator: "sum:my.custom.count.metric{type:good_events}.as_count()",
    },
    tags: [
        "foo:bar",
        "baz",
