    locations: ["aws:eu-central-1"],
    message: "Notify @pagerduty",
    name: "An API test on example.org",
    options: {
        tick_every: 900,
    },
    request: {
        method: "GET",
        url: "https://www.example.org",
    },
    requestHeaders: {
